mapper.select:
SELECT t0.id, t0.micro_id, t0.name, t0.code, t0.uri, t0.method, t0.create_by, t0.create_time, t0.update_by, t0.update_time, t1.name micro_name FROM sys_func t0 JOIN sys_micro t1 ON t0.micro_id = t1.id
AND t0.micro_id = #{microId}
AND t0.name like '%${name}%'
AND t0.code like '%${code}%'
AND t0.uri like '%${uri}%'
AND t0.method = #{method}

SELECT count(1) count FROM sys_func t0 JOIN sys_micro t1 ON t0.micro_id = t1.id
AND t0.micro_id = #{microId}
AND t0.name like '%${name}%'
AND t0.code like '%${code}%'
AND t0.uri like '%${uri}%'
AND t0.method = #{method}

mapper.insert:
INSERT INTO sys_func (
	micro_id, name, code, uri, method, create_by, create_time, update_by, update_time
) VALUES (
	#{microId}, #{name}, #{code}, #{uri}, #{method}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}
)
